        squarePath.addLine(to: CGPoint(x: 75.7, y: 14))
        squarePath.addLine(to: CGPoint(x: 75.7, y: 30))
        squarePath.addLine(to: CGPoint(x: 84.83, y: 30))
        squarePath.addLine(to: CGPoint(x: 84.83, y: 14))
        squarePath.addLine(to: CGPoint(x: 93.96, y: 14))
        squarePath.addLine(to: CGPoint(x: 93.96, y: 22))
        color2.setStroke()
