        upSawPath.addLine(to: CGPoint(x: 135, y: 15))
        upSawPath.addLine(to: CGPoint(x: 122, y: 31))
        color3.setStroke()
        upSawPath.lineWidth = 2
        upSawPath.stroke()

        //// SineWave Drawing
        let sineWavePath = UIBezierPath()
        sineWavePath.move(to: CGPoint(x: 13, y: 20.93))
        sineWavePath.addCurve(to: CGPoint(x: 18, y: 13), controlPoint1: CGPoint(x: 13, y: 20.93), controlPoint2: CGPoint(x: 12, y: 13))
